import React , { useState }from "react";
import Expenses from "./components/Expenses/Expenses";
import NewExpense from "./components/NewExpense/NewExpense";

const Dummy_expenses = [
  {
    id: "e1",
    title: "Car Wash",
    amount: 22.63,
    date: new Date(2023, 5, 12)
  },
  {
    id: "e2",
    title: "Amex Gold Card Fee",
    amount: "250",
    date: new Date(2023, 5, 10)
  }
];

function App() {

  const [expense, setExpense] = useState(Dummy_expenses)

  function addExpenseHandler(expense){
    setExpense((prev_expenses) =>{
      return [expense,...prev_expenses];
    });
  }
  // Make h2 tag center
  const h2Styles = {
    textAlign: "center",
    color: "white"
  };

  return (
    <div>
      <h2 style={h2Styles}>Welcome To ECE 459 Learning REACT.JS FOR Engineers!</h2>
      <NewExpense onAddExpense ={addExpenseHandler}/>
      <Expenses items={Dummy_expenses} />
    </div>
  );
}

export default App;
